(()=>{window.addEventListener("DOMContentLoaded",()=>{const t=new IntersectionObserver(t=>{t.forEach(t=>{const n=t.target.getAttribute("id");t.intersectionRatio>0&&(e(),document.querySelector(`aside div nav li a[href="#${n}"]`).parentElement.classList.add("active"))})});document.querySelectorAll("h1[id],h2[id],h3[id],h4[id]").forEach(e=>{t.observe(e)})});function e(){document.querySelectorAll("aside div nav li a").forEach(e=>{e.parentElement.classList.remove("active")})}})()